import React from 'react';
import {Card, Button} from "react-bootstrap";

function PageCard({article}) {

    return (
        <Card style={{ width: '21rem', height: '32rem' }}>
            <Card.Img variant="top" src={article.urlToImage && article.urlToImage} />
            <Card.Body>
                <Card.Title>{article.title && article.title.substring(0,70)}</Card.Title>
                <Card.Text>
                    {article.description && article.description.substring(0,200)}
                </Card.Text>
                <Button variant="primary" href={article.url && article.url}>Read More</Button>
            </Card.Body>
        </Card>
    );
}

export default PageCard;
