import React, {useEffect, useState} from 'react';
import SectionCard from "./SectionCard";
import {Col, Row} from "react-bootstrap";
import axios from "axios";
import {logDOM} from "@testing-library/react";

function CountrySection({country},{header}) {

    const [newsArray, setNewsArray] = useState([])


    useEffect(()=>{
        axios.get(`https://newsapi.org/v2/top-headlines?country=${country}&apiKey=b085493a09bc48d8869853e6fffd2e9f`)
            .then(res => {
                let tempArray = []
                let apiArray = res.data.articles
                for (let i = 0; i < 6; i++) {
                    tempArray.push(apiArray[i])
                }
                setNewsArray(tempArray)
            })
    },[])

    return (
        <>
        <Row className="min-vw-100">
            <h1>{header} News</h1>
        </Row>
        <Row className="min-vw-100">
            {newsArray.map((article,id)=>(
                <Col key={id} className={"md={2}"}>
                <SectionCard article={article}/>
                </Col>
            ))}
        </Row>
        </>
    );
}

export default CountrySection;
