import React, {useEffect, useState} from 'react';
import {useParams} from "react-router-dom";
import {Container} from "react-bootstrap";
import axios from "axios";
import PageCard from "./PageCard";

function CountryPage() {

    const [newsArray, setNewsArray] = useState([])

    let { id } = useParams()

    useEffect(()=>{
        axios.get(`https://newsapi.org/v2/top-headlines?country=${id}&apiKey=b085493a09bc48d8869853e6fffd2e9f`)
            .then(res => {
                let apiArray = res.data.articles
                setNewsArray(apiArray)
            })
    },[])

    return (
        <Container>
            {newsArray.map((article,id)=>(
                <PageCard key={id} article={article}/>
            ))}
        </Container>

    );
}

export default CountryPage;
